import Card from '../UI/Card';
import classes from './Cart.module.css';
import CartItem from './CartItem';
import { useSelector , useDispatch } from 'react-redux';
import { useEffect } from 'react';
import { uiActions } from '../../store/ui-slice';
import Notification from '../UI/Notification';
import { cartActions } from '../../store/cart-slice';


const Cart = (props) => {
  const dispatch = useDispatch();
  const cartItems = useSelector((state) => state.cart.items);

  useEffect(() => {
    const fetchCartData = async () => {
      dispatch(
        uiActions.showNotification({
          status: 'pending',
          title: 'Fetching..',
          message: 'Fetching cart data!',
        })
      );

      try {

        const response = await fetch('https://redux-cart-39854-default-rtdb.firebaseio.com/cart.json', {
          method: 'GET',
          body: JSON.stringify(cart),
        });

        if (!response.ok) {
          throw new Error('Fetching cart data failed.');
        }

        const responseData = await response.json();

        // Dispatch an action to update the Redux store with fetched data
        // Assuming responseData is the format you want to update the cart state
        dispatch(cartActions.replaceCart(responseData));

        dispatch(
          uiActions.showNotification({
            status: 'success',
            title: 'Success..',
            message: 'Fetched cart data successfully',
          })
        );
      } catch (error) {
        dispatch(
          uiActions.showNotification({
            status: 'error',
            title: 'Error..',
            message: 'Fetching cart data failed!',
          })
        );
      }
    };

    fetchCartData(); // Fetch data when the component mounts
  }, [dispatch]);

  return (
    <Card className={classes.cart}>
      <h2>Your Shopping Cart</h2>
      <ul>
        {cartItems.map((item) => (
          <CartItem
            key={item.itemId}
            item={{
              id: item.id,
              title: item.name,
              quantity: item.quantity,
              total: item.totalPrice,
              price: item.price,
            }}
          />
        ))}
      </ul>
    </Card>
  );
};

export default Cart;


